definition.script:
pipeline {
  agent any
  options { 
    disableConcurrentBuilds() 
  }
  stages {
    stage ("Cleanup") {
      agent {
        label "aix&&18.2&&cita_latest"
      }
      steps {
        script {
          cleanWs()
          catchError(buildResult: "UNSTABLE", stageResult: "FAILURE") {
            def NODE="${env.NODE_NAME}"
            if (("mac") == NODE.take(3)) {
              def citaDEVT="/devt/"
              def folder = "%xinD%"
              echo "node=${env.NODE_NAME}"
              if ("$NODE"=="mac3") {
                citaDEVT = "/Volumes/devt"
                folder= folder.replaceAll("(^|=)/devt/","\$1/Volumes/devt/") 
              }
              path = "/Dyalog/Dyalog-18.2.app/Contents/Resources/Dyalog/mapl"
              exists = fileExists(path)      
              if (exists) {
                echo "PLATFORM=mac, path=${path}: File exists!"
              } else {
                echo "PLATFORM=mac, path=${path}: File does not exist, trying another path"
                path = "/Applications/Dyalog-18.2.app/Contents/Resources/Dyalog/mapl"
                exists = fileExists(path)          
                if (exists) {
                  echo "PLATFORM=mac, path=${path}: File exists!"
                } else {
                  error "PLATFORM=mac, path=${path}: File does not exist, giving it up!"
                }
              }                
              echo "executing $path load=$citaDEVT/apltools/CITA/CITA-Tests/cleanup.aplf configfile=$citaDEVT/apltools/CITA/CITA-Tests/cleanup.dcfg FOLDER=$folder"              
              sh "$path load=$citaDEVT/apltools/CITA/CITA-Tests/cleanup.aplf configfile=$citaDEVT/apltools/CITA/CITA-Tests/cleanup.dcfg FOLDER=$folder"              
            } else {
              // use this for AIX:
              sh "/opt/mdyalog/18.2/64/unicode/p8/mapl load=/devt/apltools/CITA/CITA-Tests/cleanup.aplf configfile=/devt/apltools/CITA/CITA-Tests/cleanup.dcfg FOLDER=%xinD%"              
              sh "exit 0"  // end this step w/o problem-status, as it would avoid execution of next steps...
            }
          }
       }
      }
    }
    stage ("Run Tests") {
      parallel {
        %STAGES%  
      }
    }
    stage ("Unlock") {
      agent {
        label "pi&&18.2&&cita_latest"  // do not run this on AIX
      }
      steps {
        script {
          catchError(buildResult: "UNSTABLE", stageResult: "FAILURE") {
            def LOCKFILE="%LOCKFILE%"
            if (LOCKFILE != "") {
              sh "rm $LOCKFILE"
            }
          }
        } 
      }
    }
  }
}
